import http from "../http"
import { useState } from "react"
import { useNavigate } from "react-router-dom";

export default function Create(){

    const [inputs ,setInputs] = useState({});
    const handleChange = (event) => {
        const todo   = event.target.name;
        const value  = event.target.value;
        setInputs(values => ({...values,[todo]:value}))
    }

    const  submitForm = () =>{
       http.post('http://localhost:8000/api/todo',inputs).then(res=>{
        useNavigate('/');
       })
    }
    return (
        <div>
            <h4>Add Todo</h4>
            <div className="row">
                <div className="col-sm-6">
                    <div className="card p-4">
                        <label>Todo Name</label>
                        <input type="text" name="name"  className="form-control mb-2"
                          value ={ inputs.todo } onChange={handleChange}/>
                       <button type="button" onClick={submitForm} className="btn btn-info mt-2">Create</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
