import http from "../http";
import { useState } from "react"
import App from '../App.css'
export default function View(){

    const api_details ={
        url: "http://api.openweathermap.org/data/2.5/",
        api_key:"60bbd59ec7556e88c0f6b5a2080aebaa"
    }

    const [inputs ,setInputs] = useState({});
    const [data1 ,setWeather] = useState([]);
    let months_year=["January","February","March","April","May","June","July","August","September","October","November","December"];
    let days_week=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
    let time = new Date();
    let day=days_week[time.getDay()];
    let pdate=time.getDate();
    let month=months_year[time.getMonth()];
    let year=time.getFullYear();



    const handleChange = (event) => {
        const todo   = event.target.name;
        const value  = event.target.value;
        setInputs(values => ({...values,[todo]:value}))
    }
    const weather=[];
    const submitForm = () =>{
        // var data = (`${api_details.url}weather?q=${inputs.name}&units=metric&APPID=${api_details.api_key}`)
        http.get(`${api_details.url}weather?q=${inputs.name}&units=metric&APPID=${api_details.api_key}`).then(res=>{
            setWeather(res.data);
            console.log(data1.sys.country);
        })


    };


    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-4">

                </div>
                <div className="col-sm-4">
                    <div className="heading">
                        <h2>Check Weather</h2>
                    </div>
                    <div className="input">
                        <input type="text" name="name"  className="form-control mb-2"
                            value ={ inputs.todo } onChange={handleChange} placeholder="enter city name"/>
                        <button  type="button" onClick={submitForm} className="btn btn-info mt-2">Search</button>
                    </div>
                    <div className="box">
                        <div className="location">
                            <div className="location-city"> <span>{data1.sy} </span> {data1.name} </div>
                            <div className="location-date"> {day} {pdate} {month} {year}</div>
                        </div>
                        <div className="temperature">
                            <div className="temperature-temp"><span>39 </span></div>
                            <div className="temperature-type">Cloud</div>
                            <div className="temperature-range">30-39</div>
                        </div>
                    </div>
                </div>

                <div className="col-sm-4">

                </div>
            </div>

	    </div>
    )
}
