import http from "../http";
import {useState,useEffect} from 'react';
export default function Home(){

    const [todos,setTodos] = useState([]);
    useEffect(() => {

        fetchallTodos();

    }, []);

    const deleteTodo = (id) => {
        console.log(id);
        http.delete('http://localhost:8000/api/todo/'+id).then(res=>{
            fetchallTodos();
        })
    }


    const fetchallTodos = () =>{
        http.get('http://localhost:8000/api/todo').then(res=>{
            console.log(res.data);
            setTodos(res.data);

        })
    }
    return (
        <div>
            <div className="row">
                <div className="col-sm-2">
                </div>
                <div className="col-sm-6">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>S.No</th>
                                <th>Todo Name</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                           {todos.map((todo,index)=>(
                               <tr key={todo.id}>
                                    <td>{++index}</td>
                                    <td>{todo.name}</td>
                                    <td>
                                    <button type="button" className="btn btn-danger"
                                    onClick={()=>{deleteTodo(todo.id)}}
                                    >Delete</button>
                                    </td>
                                </tr>
                           ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
