import React, { useState } from 'react'
import axios from 'axios'
import '../style.css'; 

function App() {

    const api_details={
        url: "http://api.openweathermap.org/data/2.5/",
        api_key:"60bbd59ec7556e88c0f6b5a2080aebaa"
    }
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = (`${api_details.url}weather?q=${location}&units=metric&APPID=${api_details.api_key}`)

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? <h1>{data.main.temp.toFixed()}°C</h1> : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
          {data.name !== undefined &&
            <div className="bottom r">
                <div className="feels">
                {data.main ? <p className='bold'>{(((data.main.feels_like.toFixed()) -32)/1.8)}°C</p> : null}
                <p>Feels Like</p>
                </div>
                <div className="humidity">
                {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
                <p>Humidity</p>
                </div>
                <div className="wind">
                {data.wind ? <p className='bold'>{data.wind.speed.toFixed()} MPH</p> : null}
                <p>Wind Speed</p>
                </div>
            </div>
            }
        </div>
      </div>
    </div>
  );
}

export default App;
