import http from "../http";
import { useState } from "react"
export default function View(){

    const api_details ={
        url: "http://api.openweathermap.org/data/2.5/",
        api_key:"60bbd59ec7556e88c0f6b5a2080aebaa"
    }

    const [inputs ,setInputs] = useState({});

    const handleChange = (event) => {
        const todo   = event.target.name;
        const value  = event.target.value;
        setInputs(values => ({...values,[todo]:value}))
    }

    const submitForm = () =>{
        var data = (`${api_details.url}weather?q=${inputs}&units=metric&APPID=${api_details.api_key}`)
        // http.get(`${api_details.url}weather?q=${inputs}&units=metric&APPID=${api_details.api_key}`).then(res=>{
        //     console.log(res.data);
        // })
        console.log(data);

    };


    return (
        <div className="container">
            <div className="heading">
                <h1>Check Weather</h1>
            </div>
            <div className="input">
            <input type="text" name="name"  className="form-control mb-2"
                value ={ inputs.todo } onChange={handleChange} placeholder="enter city name"/>
            <button type="button" onClick={submitForm} className="btn btn-info mt-2">Create</button>
            </div>
            <div className="box">
            <div className="location">
                <div className="location-city"></div>
                <div className="location-date"></div>
            </div>
            <div className="temperature">
                <div className="temperature-temp"><span></span></div>
                <div className="temperature-type"></div>
                <div className="temperature-range"></div>
            </div>
            </div>
	    </div>
    )
}
